import React, { useState } from 'react';
import Axios from 'axios';
import './Style/App.css';
import Modal from './ModalComponent';

let cards = [
  // valid card
  {
    card_number: '1111111111111111',
    cvv: 789,
    expiry_date: '01/18',
  },
  // invalid card
  {
    card_number: '4111111111111234',
    cvv: 123,
    expiry_date: '01/20',
  },
];

const finalFirtsCard = cards[card_number][0].substr(12,14);
const finalSecondCard = cards[card_number][0].substr(12,14)

export default function CarregaUsers() {

  const[users, setUsers] = useState([]);
  const[isModalVisible, setIsModalVisible] = useState(false);

  // Axios.get no mocky para receber o objeto com uma lista de usuários. 
  Axios.get('https://www.mocky.io/v2/5d531c4f2e0000620081ddce').then(answer => {
    const user = answer.data;
    setUsers(user);
  });


return (
  users.map((item, key) => 
  <div className='usersStyle'>
    <div className='itensLeft'>
    <div className='userPic'>
      <img id="imgPic" src={item.img} alt=''/>
    </div>
    <div className='userName'>
      <div>
        Nome do Usuario: {item.name}
      </div>
      <div>
        ID: {item.id} - Username: {item.username}
      </div>
      </div>
    </div>
    <div className='buttonPay'>
      <button id='button' onClick={() => setIsModalVisible(true)}>Pagar</button>
      {isModalVisible ? <Modal/> : true}
    </div>
  </div>
  )
  )
}
