import React, {useState} from 'react';
import './Style/App.css';
import Axios from 'axios';
import ListUsers from './ListUsersComponent';

export default function Modal() {

    const[cardsProfile, setCardsProfile] = useState([]);

    let cards = [
    // valid card
    {
        card_number: '1111111111111111',
        cvv: 789,
        expiry_date: '01/18',
    },
    // invalid card
    {
        card_number: '4111111111111234',
        cvv: 123,
        expiry_date: '01/20',
    },
    ];

    Axios.get(cards).then(answer => {
    const card = answer.data;
    setCardsProfile(card);
    });

    const numberCard = cardsProfile.map((key) =>
    key.card_number.substring(12,16)
    )

    const Modal = () => {
        return(
            <div>
                <div className='HeaderPay'>Pagamento para </div>
                <div className='BodyPay'>
                    <input className='InputValue'></input>
                    <select className='SelectCard'>
                        <option value='selection' disabled selected>Selecione o cartão</option>
                        <option value='firstCard'>Cartão com final {numberCard}</option>
                        <option value='secondCard'>Cartão com final {numberCard}</option>
                    </select>
                    <button id='ButtonPayModal'>Pagar</button>
                </div>
            </div>
        )
    }
}
