import React, { useState } from 'react';
import './Style/App.css';
import ListUser from './ListUsersComponent';

const Modal = ({ id='containerModal', onClose = () => {}, children}) => {

    const [cardsProfile, setCardsProfile] = useState([]);

    let cards = [
        // valid card
        {
            card_number: '1111111111111111',
            cvv: 789,
            expiry_date: '01/18',
        },
        // invalid card
        {
            card_number: '4111111111111234',
            cvv: 123,
            expiry_date: '01/20',
        },
    ];

    const handleOutsideClick = (e) => {
        if(e.target.id == id) onClose();
    };

    return (
        <div id={id} className='boxModal' omClick={handleOutsideClick}>
            <div className='containerModal'>
                <div className='containerHeaderModal'>
                    <div className='headerPay'>Pagamento para {window.nome}</div>
                </div>
                <div className='containerBodyModal'>
                    <input className='inputValue' placeholder='R$ 0,00' onKeyUp="valueFormated()"></input>
                    <select value={cardsProfile} onChange={e => setCardsProfile(e.target.value)} className='selectCard'>
                        <option value='selection' disabled selected>Selecione o cartão</option>
                        {cards.map((item, index) => (
                            <option value={item.key}>Cartão com final {item.card_number.substring(12, 16)}</option>
                        ))}
                    </select>
                    <button id='buttonPayModal'>Pagar</button>
                    <div className='content'>{children}</div>
                </div>
            </div>
        </div>
    )
}

function valueFormated() {
    inputValue = document.getElementsByClassName(inputValue)
    let valueOption = inputValue.value;
    valueOption = inputValue + "";
    valueOption = parseInt(inputValue.replace(/[\D]+/g, ""));
    valueOption = inputValue + "";
    valueOption = inputValue.replace(/([0-9]{2})$/g, ",$1");

    if (valueOption.length > 6) {
      valueOption = valueOption.replace(/([0-9]{3}),([0-9]{2}$)/g, ".$1,$2");
    }
    if (valueOption.length > 10) {
      valueOption = valueOption.replace(
        /([0-9]{3}).([0-9]{3}),([0-9]{2})$/g,
        ".$1.$2,$3"
      );
    }

    inputValue.value = valueOption;
    if (valueOption === "NaN") {
        inputValue.value = "";
    }
  }

export default Modal;
